import React from 'react';
import { Container, Grid, Typography, Card, CardContent, CardMedia } from '@mui/material';

const articles = [
  {
    title: 'Billionaire Charoen To Open Nobu Restaurant Atop Bangkok Skyscraper',
    image: 'path_to_image_1', // Add the actual image path here
    content: 'Billionaire Charoen Sirivadhanabhakdi’s Asset World Plans To Open A Nobu Restaurant On The Roof Deck Of Its Empire Mixed-Used Office, Residential And Commercial Tower In Central Bangkok To Tap On The Tourism Boom.',
  },
  // ...add more articles here
];

export default function News() {
  return (
    <Container>
      <Typography variant="h3" component="h1" gutterBottom>
        News
      </Typography>
      <Grid container spacing={4}>
        {articles.map((article, index) => (
          <Grid item key={index} xs={12} sm={6} md={4}>
            <Card>
              <CardMedia
                component="img"
                alt={article.title}
                height="140"
                image={article.image}
                title={article.title}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {article.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {article.content}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          
        ))}
      </Grid>
      
    </Container>
  );
}
